mapper.insert:
    	INSERT INTO tb_post(
    		  title
	        , contents
	        , writer
	        , view_cnt
	        , notice_yn
	        , delete_yn
    	) VALUES (
    			#{title}
    		,	#{contents}
        	,	#{writer}
            , 	0
            , 	#{noticeYn}
            , 	0
    	)

mapper.update:
UPDATE tb_post
SET
      modified_date = NOW()
    , title = #{title}
    , content = #{content}
    , writer = #{writer}
    , notice_yn = #{noticeYn}
WHERE
    id = #{id}
